import React from "react";
import { Link, useLocation } from "react-router-dom";
import { LuChefHat } from "react-icons/lu";
import "./navBar.css";

function NavBar() {
  const location = useLocation();
  

  function handleLogout(e)
  {
    let data = new FormData();
data.append('email', 'andrija@gmail.com');
data.append('password', 'andrija123');
data.append('name', 'andrija');

let config = {
  method: 'post',
  maxBodyLength: Infinity,
  url: 'http://127.0.0.1:8000/api/logout',
  headers: { 
    'Authorization': '••••••', 
    ...data.getHeaders()
  },
  data : data
};

axios.request(config)
.then((response) => {
  console.log(JSON.stringify(response.data));
})
.catch((error) => {
  console.log(error);
});

   
  }
  return (
    <div className="navBar">
      <Link
        to="/"
        className={`titl ${location.pathname === "/" ? "active" : ""}`}
      >
        <LuChefHat className="ikonica" /> Receptorijum
      </Link>
      <Link
        to="/svajela"
        className={`SvaJela ${
          location.pathname === "/svajela" ? "active" : ""
        }`}
      >
        Sva Jela
      </Link>
      <Link
        to="/Contact"
        className={`Kontakt ${
          location.pathname === "/Contact" ? "active" : ""
        }`}
      >
        Kontakt
      </Link>
      {
      window.sessionStorage.getItem("auth_token") == null ?
      <Link
        to="/login"
              className={`Login ${
                  location.pathname === "/login" ? "active" : ""}`}
      >
        Login 
        </Link>
        :
        <Link
        to="/login"
        onClick={handleLogout}
              className={`Logout ${
                  location.pathname === "/logout" ? "active" : ""}`}
      >
        Logout
      
      </Link>
      }
      {/* <Link
        to="/register"
        className={`Register ${
          location.pathname === "/register" ? "active" : ""
        }`}
      >
        Register
      </Link>{" "} */}
    </div>
  );
}

export default NavBar;
